import React,{useState} from "react";

function CreateArea(props) {

  const [note, setnote] = useState({
    title:"",
    content:""
  }); 

  function takeNote(event) {
    const value = event.target.value;
    const type = event.target.name;
    setnote(prevValue => {
      if(type==="title"){
        return {
          title:value,
          content:prevValue.content
        };
      }else {
        return {
          title:prevValue.title,
          content:value
        };
      }
    })
  }

  return (
    <div>
      <form>
        <input name="title" placeholder="Title" onChange={takeNote} value={note.title}/>
        <textarea name="content" placeholder="Take a note..." rows="3"  onChange={takeNote} value={note.content}/>
        <button 
        onClick={() => {
          props.addItem(note)
          }}
          >
          Add
          </button>
      </form>
    </div>
  );
}

export default CreateArea;
